    for (uint i = 0; i < lightCount; ++i)
    {
        // Calculate per-light radiance
        vec3  L;
        computeLightDirection(i, vs_FragmentPosition, L);

        float attenuation;
        computeLightAttenuation(i, vs_FragmentPosition, attenuation);

        vec3 radiance = Lights.data[i].color.rgb * attenuation;
        vec3 currLo;
        specularContribution(radiance, L, V, N, F0, Albedo, Metallic, Roughness, currLo);
        Lo += currLo;
    }
}
*/

float alpha       = (N.y + 1.0) / 2.0;
vec3 ambientColor = vec3(1.0); //mix(ambientColorBottom, ambientColorTop, alpha);

float posNdotV  = max(dot(N, V), 0.0);

vec2 brdf       = texture(textures[2], vec2(posNdotV, 1.0-Roughness)).rg;
vec3 reflection;
prefilteredReflection(ambientColor, radianceMapIndex, R, Roughness, reflection);
vec3 irradiance = texture(cubemaps[irradianceMapIndex], N).rgb * ambientColor;

vec3 diffuse    = irradiance * Albedo;

vec3 F;
fresnelSchlickRoughness(posNdotV, F0, Roughness, F);

vec3 specular   = reflection * (F * brdf.x + brdf.y);

vec3 kD         = 1.0 - F;
kD             *= 1.0 - Metallic;
vec3 ambient    = (kD * diffuse + specular) * AmbientOcclusion;

color           = ambient + Lo;

